CREATE TABLE `noticias_tb` (
  `id_noticia` int(11) NOT NULL,
  `data_user` datetime DEFAULT CURRENT_TIMESTAMP,
  `data_update` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `tipo` char(3) COLLATE utf8_general_ci DEFAULT NULL,
  `titulo` tinytext COLLATE utf8_general_ci,
  `resumo` mediumtext COLLATE utf8_general_ci,
  `texto` longtext COLLATE utf8_general_ci,
  `imagem` varchar(30) COLLATE utf8_general_ci DEFAULT NULL,
  `destaque` enum('sim','nao') COLLATE utf8_general_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

